# Operator Assigment

a = 5
print('nilai a = ',a) 

a += 5 #artinya adalah a = a + 5
print('nilai a += ',a) 

a -= 5 #artinya adalah a = a - 5
print('nilai a = ',a) 

a *= 5 #artinya adalah a = a * 5
print('nilai a *= ',a) 

a /= 5 #artinya adalah a = a / 5
print('nilai a /= ',a) 


b = 10
print('\nnilai b :',b) 

b %= 10
print('\nnilai b %=',b, 'maka nilai b menjadi ',b) 

b = 10
print('\nnilai b :',b) 

b //= 3
print('\nnilai b //=',b, 'maka nilai b menjadi ',b) 


a = 10
print('\nnilai a :',a) 

a **= 10
print('\nnilai a **=',a, 'maka nilai a menjadi ',a) 


# operator bitwise
# OR
c= True
print('\nnilai c = ',c)
c|= True
print('\nnilai c |= ',c)
c = False
print('\nnilai c = ',c)
c |= False
print('\nnilai c |= ',c)


# AND
c= True
print('\nnilai c = ',c)
c&= True
print('\nnilai c &= ',c)
c = False
print('\nnilai c = ',c)
c &= False
print('\nnilai c &= ',c)


# XOR
c= True
print('\nnilai c = ',c)
c^= True
print('\nnilai c ^= ',c)
c = False
print('\nnilai c = ',c)
c ^= False
print('\nnilai c ^= ',c)





