# Operasi Artimatika

# Operasi Penambahan
a = 4
b = 2
hasil = a+b
print(a,"+",b,"=", hasil)

# Operasi Pengurangan
a = 4
b = 2
hasil = a-b
print(a,"-",b,"=", hasil)

# Operasi Pembagian
a = 4
b = 2
hasil = a / b
print(a,"/",b,"=", hasil)

# Operasi Pengurangan
a = 4
b = 2
hasil = a*b
print(a,"*",b,"=", hasil)

# Operasi eksponen
a = 4
b = 2
hasil = a**b
print(a,"**",b,"=", hasil)

# Operasi Modulus % (sisa bagi)
a = 9
b = 2
hasil = a%b
print(a,"%",b,"=", hasil)


# Operasi floor division
a = 5
b = 2
hasil = a//b
print(a,"//",b,"=", hasil)

# prioritas operasi, operational precedence
'''
    1.()
    2. exponen ***
    3. perkalian dan teman-teman * / ** % //
    4. pertambahan dan pengurangan + -
'''

x = 3
y = 2
z = 4

hasil = x**y*z+x/y-y%z//x
print(x,'**',y,'*',z,'+',x,'/',y,'-',y,'%',z,'//',x, hasil)



